import { constants } from 'ethers';
import { useState, useEffect } from 'react';
/**
 * Gets address from given ENS name and provider
 * @param provider
 * @param ensName
 * @returns
 */
export const useResolveEnsName = (provider, ensName) => {
    const [address, setAddress] = useState(constants.AddressZero);
    useEffect(() => {
        if (provider) {
            void provider.resolveName(ensName).then((resolvedAddress) => setAddress(resolvedAddress));
        }
    }, [provider, ensName]);
    return address;
};
